"use client";
import React, { useState, useEffect } from "react";
import {
  Timer,
  Play,
  Pause,
  RotateCcw,
  Coffee,
  Bell,
  Volume2,
  VolumeX,
} from "lucide-react";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Progress } from "@/components/ui/progress";
import { Switch } from "@/components/ui/switch";
import { Alert, AlertDescription } from "@/components/ui/alert";

const PomodoroTimer = () => {
  const [timeLeft, setTimeLeft] = useState(25 * 60);
  const [isRunning, setIsRunning] = useState(false);
  const [mode, setMode] = useState("work");
  const [workDuration, setWorkDuration] = useState(25);
  const [breakDuration, setBreakDuration] = useState(5);
  const [sessions, setSessions] = useState(0);
  const [soundEnabled, setSoundEnabled] = useState(true);
  const [autoStartBreaks, setAutoStartBreaks] = useState(false);
  const [showNotification, setShowNotification] = useState(false);

  // Calculate progress percentage
  const totalSeconds = mode === "work" ? workDuration * 60 : breakDuration * 60;
  const progress = ((totalSeconds - timeLeft) / totalSeconds) * 100;

  useEffect(() => {
    let interval;
    if (isRunning && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft((time) => time - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      handleSessionComplete();
      playNotificationSound();
    }
    return () => clearInterval(interval);
  }, [isRunning, timeLeft]);

  const playNotificationSound = () => {
    if (soundEnabled) {
      const audio = new Audio(
        "data:audio/wav;base64,"
      );
      audio.play();
    }
  };

  const handleSessionComplete = () => {
    const newMode = mode === "work" ? "break" : "work";
    setMode(newMode);
    setTimeLeft(newMode === "work" ? workDuration * 60 : breakDuration * 60);
    setIsRunning(autoStartBreaks);
    setSessions((s) => (newMode === "work" ? s + 1 : s));
    setShowNotification(true);
    setTimeout(() => setShowNotification(false), 3000);
  };

  const handleReset = () => {
    setIsRunning(false);
    setTimeLeft(workDuration * 60);
    setMode("work");
    setSessions(0);
    setShowNotification(false);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60)
      .toString()
      .padStart(2, "0");
    const secs = (seconds % 60).toString().padStart(2, "0");
    return `${mins}:${secs}`;
  };

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Timer className="h-6 w-6" />
            Pomodoro Timer
          </div>
          <div className="flex items-center gap-2 text-sm">
            <Coffee className="h-4 w-4" />
            <span className="font-mono">{sessions}</span>
          </div>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {showNotification && (
          <Alert className="mb-4">
            <AlertDescription>
              {mode === "work"
                ? "Break time! Take a rest."
                : "Break is over! Time to focus."}
            </AlertDescription>
          </Alert>
        )}

        <div className="text-center space-y-2">
          <div className="text-6xl font-mono font-bold tracking-wider">
            {formatTime(timeLeft)}
          </div>
          <div className="text-sm text-muted-foreground capitalize">
            {mode} session
          </div>
        </div>

        <Progress value={progress} className="h-2" />

        <div className="flex items-center justify-center space-x-4">
          <Button
            variant="outline"
            size="icon"
            onClick={handleReset}
            className="w-12 h-12"
          >
            <RotateCcw className="h-5 w-5" />
          </Button>
          <Button
            size="icon"
            onClick={() => setIsRunning(!isRunning)}
            className="w-16 h-16"
          >
            {isRunning ? (
              <Pause className="h-8 w-8" />
            ) : (
              <Play className="h-8 w-8" />
            )}
          </Button>
          <Button
            variant="outline"
            size="icon"
            onClick={() => setSoundEnabled(!soundEnabled)}
            className="w-12 h-12"
          >
            {soundEnabled ? (
              <Volume2 className="h-5 w-5" />
            ) : (
              <VolumeX className="h-5 w-5" />
            )}
          </Button>
        </div>

        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <span>Auto-start breaks</span>
            <Switch
              checked={autoStartBreaks}
              onCheckedChange={setAutoStartBreaks}
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Work Duration</label>
              <Select
                value={workDuration}
                onValueChange={(value) => {
                  setWorkDuration(Number(value));
                  if (mode === "work") {
                    setTimeLeft(Number(value) * 60);
                  }
                }}
              >
                <SelectTrigger>
                  <SelectValue>{workDuration} minutes</SelectValue>
                </SelectTrigger>
                <SelectContent>
                  {[15, 20, 25, 30, 35, 40, 45, 50, 55, 60].map((duration) => (
                    <SelectItem key={duration} value={duration}>
                      {duration} minutes
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium">Break Duration</label>
              <Select
                value={breakDuration}
                onValueChange={(value) => {
                  setBreakDuration(Number(value));
                  if (mode === "break") {
                    setTimeLeft(Number(value) * 60);
                  }
                }}
              >
                <SelectTrigger>
                  <SelectValue>{breakDuration} minutes</SelectValue>
                </SelectTrigger>
                <SelectContent>
                  {[5, 10, 15, 20, 25, 30].map((duration) => (
                    <SelectItem key={duration} value={duration}>
                      {duration} minutes
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default PomodoroTimer;
